      </div>
      <div className="row">
        <React.Fragment>
          {array.map((item, i) => (
            <div className="col-md-4 mb-5 text-dark" key={i}>
              <div
                className="card123 pb-3 pt-3"
                style={{
                  width: "300px",
                  height: "150px",
                  backgroundColor: item.backgroundColor,
