import { ADD_BUTTON } from "../actions/expensives_action";
import { DELETE_LIST } from "../actions/expensives_delete";

const expensive_Reducer = (state = [], action) => {
  switch (action.type) {
    case ADD_BUTTON:
      // console.log("state", state, action.payload);
      return [...state, action.payload];
    //   if (action.payload.type === "income") {
    //     return [...state, action.payload.amount];
    //   }
    case DELETE_LIST:
      //return [...state, action.payload];
      var list = state.filter((data) => action.payload !== data.index);
      //return [...state, list];
      return [...state, list, console.log("yes", state)];

    default:
      return state;
  }
};

export default expensive_Reducer;
