import { ADD_BUTTON } from "../actions/expensives_action";
import { DELETE_LIST } from "../actions/expensives_delete";

const expensive_Reducer = (state = [], action) => {
  switch (action.type) {
    case ADD_BUTTON:
      return [...state, action.payload];
    //   }
    case DELETE_LIST:
      //return [...state, action.payload];
      // var list = state.filter(data => action.payload !== data.index);
      //return [...state, list];
      //return [...state, list];
      var items;
      return [
        ...state,
        (items = state.items.filter((data) => action.payload !== data.index)),
      ];

    default:
      return state;
  }
};

export default expensive_Reducer;
